"css`
    :host {
      display: block;
      position: relative;
      height: 300px;
    }
    #controls {
      position: absolute;
      bottom: 0;
      width: 100%;
      display: flex;
    }
    mwc-slider {
      flex-grow: 1;
    }
    mwc-linear-progress,
    #error-message {
      position: absolute;
      top: 0;
      width: 100%;
    }
    #error-message {
      padding-top: 10px;
      color: red;
      text-align: center;
    }
    canvas {
      outline: none;
    }
  `"

"css`
    :host {
      --mdc-theme-primary: green;
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(400px, 488px));
      grid-gap: 24px 48px;
      justify-content: center;
    }

    :host > div {
      flex: 1;
    }

    span {
      font-style: bold;
    }
    #src-label {
      margin-left: 15px;
    }
    #src-label,
    #step-label {
      font-weight: bold;
    }

    mwc-select {
      vertical-align: middle;
    }

    input[type='number'] {
      padding: 16px;
      font-size: 16px;
      font-family: Roboto;
      color: rgba(0, 0, 0, 0.87) !important;
      outline-color: var(--mdc-select-outlined-idle-border-color) !important;
    }
    input[type='number']:focus {
      outline-color: var(--mdc-theme-primary) !important;
    }

    .code {
      background: #272822;
      padding: 1em;
      font-family: Consolas, Monaco, 'Andale Mono', 'Ubuntu Mono', monospace;
      color: #f8f8f2;
    }
    .code .tag {
      color: #f92672;
    }
    .code .attribute, .code .string {
      color: #a6e22e;
    }
    .code .value {
      color: #e6db74;
    }
    .code .keyword {
      color: #66d9ef;
    }
  `"
